import React, {useState, useEffect} from 'react'
import '../index.css'
import TweetBox from './TweetBox'
import Post from './Post'
import db from '../config/firebase'


function Feed() {
    const [posts, setPosts] = useState([])

    useEffect(() => {
        db.collection('posts').onSnapshot(snapshot => (
            setPosts(snapshot.docs.map(doc => doc.data()))
        ))
    }, [])


    return (
        <div className="feed">
            <div className="feed_header">
                <h2>Home</h2>
            </div>

            <TweetBox />

            {/* Post */}
            <Post 
                displayName="Ranjan Roy"
                username="ranjan3606"
                verified={true}
                text="YOOOOOOOO IS Working"
                avatar="https://www.filmibeat.com/ph-big/2019/07/ismart-shankar_156195627930.jpg"
                image="https://www.ntvtelugu.com/uploads/images/2019/07/image_default_487965d2c44f95643a.jpg"
            />
        </div>
    )
}

export default Feed
